        $.each(drillData, function (key, values) {
            statisticsData[key] = values.reduce(function (memory, value) {
                return memory + value['y'];
            }, 0);
        });
        statisticsData.click = statisticsData.click * 50;
        statisticsData.ctr = statisticsData.impression > 0 ? (statisticsData.click / 50) / statisticsData.impression : 0;
        statisticsData.ctr = Math.round(statisticsData.ctr * 10000000);
        statisticsData.cost = statisticsData.cost * 5;
        statisticsData.cpm = statisticsData.impression > 0 ? statisticsData.cost / 5 / statisticsData.impression : 0;
        statisticsData.cpm = Math.round(statisticsData.cpm * 10000000);
