function checkLogin() {
    if (mf.authority.isUserLogin()) {
        $('#Nav1, #Nav2, .header-login').show();
        mf.updateNav1.__called = 0;
        er.locator.redirect(er.config.DEFAULT_INDEX + '~force=1');
        return true;
    }
    return false;
}
(function () {
    mf.index.index.login = new er.Action({
        model: mf.index.index.model.login,
        view: new er.View({
            template: 'mf_index_index_login',
            UI_PROP: {}
        }),
        STATE_MAP: {},

        onenter: function () {
            console.log('onenter');
            mf.onenter();
            $('#Nav1, #Nav2, .header-login').hide();
            var username = T.cookie.get(mf.cookieKeyMap.name) || '';
            this.model.set('username', username);
        },
        onentercomplete: function () {
            mf.loaded();
            if (!this.model.get('force')) {
                checkLogin();
            }
        },
        onafterrender: function () {
            console.log('onafterrender');
            var action = this;
            var V = mf.m.validate();

            mf.fixPassword('password', 'show_password');
            if (action.model.get('pwd')) {
                $(esui.get('show_password').main).hide();
                $(esui.get('password').main).show();
            }

            V.reg(
                {
                    id: 'username',
                    rule: [
                        {
                            tpl: 'notNull', msg: '用户名不能为空'
                        }
                    ]
                }
            );
            V.reg(
                {
                    id: 'password',
                    rule: [
                        {
                            tpl: 'notNull', msg: '密码不能为空'
                        }
                    ]
                }
            );
            var infoDialog;
            esui.get('submit').onclick = function () {
                if (!action.validateForm() || !V.check()) {
                    return;
                }
                mf.loading();
                mf.parallelAjax(
                    {
                        url:'/login',
                        type: 'POST',
                        //requestType: 'form',
                        data: {
                            username: esui.get('username').getValue(),
                            password: esui.get('password').getValue()
                        }
                    },
                    function (result) {
                        // 暂时这样吧，用户信息以后再加入
                        var user = {
                            authority : 255
                        };
                        user.authority = parseInt(user.authority || 0);
                        if (user.authority > 0) {
                            user.authority |= 1;
                        }

                        var expObj = {expires: 3600000 * 24 * 15};
                        T.cookie.set(mf.cookieKeyMap.name, esui.get('username').getValue(), expObj);
                        T.cookie.set(mf.cookieKeyMap.authority, user.authority);
                        mf.authority.parse(user.authority);
                        if (!checkLogin()) {
                            infoDialog = esui.Dialog.alert(
                                {
                                    title: '登录失败',
                                    content: '请联系管理员'
                                }
                            );
                        }
                    }
                );
            };

            $('body').unbind('keypress.login')
                .bind(
                'keypress.login', function (e) {
                    if (e.keyCode === 13) {
                        if (infoDialog) {
                            infoDialog.oncommand();
                            infoDialog = null;
                        } else {
                            esui.get('username').main.blur();
                            esui.get('password').main.blur();
                            esui.get('submit').onclick();
                        }
                    }
                }
            );

            //esui.get('register').onclick = mf.m.utils.nextTickWrapper(function () {
            //    er.locator.redirect('/index/register');
            //});
        },
        onleave: function () {
            $('body').unbind('keypress.login');
        }
    });
})();