(function () {
    mf.index.report.dayUnit = new er.Action({
        model: mf.index.report.model.dayUnit,
        view: new er.View({
            template: 'mf_index_report_dayUnit',
            UI_PROP: {
                dayUnitDate: {
                    range: mf.index.reportRange
                }
            }
        }),
        STATE_MAP: {
            date: mf.getDateString(mf.getDate(-1)),
            page: 0,
            pageSize: mf.PAGER_MODEL.pageSizes[0].value,
            pageSizes: mf.PAGER_MODEL.pageSizes
        },

        onenter: function () {
            console.log('onenter');
            mf.onenter();
        },
        onafterrepaint: function () {
            console.log('onafterrepaint');
        },
        onafterrender: function () {
            console.log('onafterrender');
            var action = this;
            var model = action.model;

            var date = esui.get('dayUnitDate');
            date.onchange = mf.m.utils.nextTickWrapper(function (value) {
                console.log('date change', value);
                mf.subActionRefresh(action, {
                    valueAsDate: value,
                    date: mf.getDateString(value)
                });
            }, date);

            var dayUnitListTable = esui.get('dayUnitList');
            dayUnitListTable.onsort = mf.m.utils.nextTickWrapper(function (orderField, order) {
                var orderBy = orderField.field;
                order = order == 'asc' ? 1 : -1;
                dayUnitListTable.datasource.sort(function (a, b) {
                    return a[orderBy] > b[orderBy] ? order : -order;
                });
                dayUnitListTable.render();
            }, dayUnitListTable);
        },
        onentercomplete: function () {
            console.log('onentercomplete');
            var action = this;
            var model = action.model;
            var $element = $('#dayUnitChart');
            mf.m.highchart_drill_cost('#dayUnitChart',
                model.get('lists'), model.get('date'),
                function (event) {
                    if (event.point.name) {
                        $element.attr({
                            'data-cmd': 'step_unit',
                            'data-unit': event.point.unit_id,
                            'data-name': event.point.name
                        });
                        $element.trigger('click');
                        $element.attr({
                            'data-cmd': null,
                            'data-unit': null,
                            'data-name': null
                        });
                    }
                }
            );
            var data = model.get('lists');
            if (data.length) {
                var total = data.reduce(function (memory, n) {
                    memory.impression += n.impression || 0;
                    memory.click += n.click || 0;
                    memory.cost += n.cost || 0;
                    return memory;
                }, {
                    os_id: null,
                    name: '总计',
                    impression: 0,
                    click: 0,
                    cost: 0,
                    ctr: 0,
                    cpm: 0
                });
                if (total.impression) {
                    total.ctr = total.click / total.impression * 100;
                    total.cpm = total.cost / total.impression * 1000;
                }
            }

            mf.mockPager(data, {
                pager: esui.get('dayUnitPager'),
                pageSizer: esui.get('dayUnitPageSize'),
                table: esui.get('dayUnitList')
            }, {
                afterSort: function (dataList, targets, opt) {
                    total && (targets.table.datasource || []).unshift(total);
                }
            })();
        }
    });
})();