(function () {
    var FIELDS = function (model) {
        return [
            {
                "field": "idea_id",
                "title": "创意ID",
                "content": function (item, index) {
                    return item.idea_id ? '<a data-cmd="step_idea" data-idea="' + item.idea_id + '" data-name="'
                                          + item.name + '">' + item.idea_id + '</a>' : '-';
                },
                "sortable": 1
            },
            {
                "field": "name",
                "title": "创意名称",
                "content": function (item, index) {
                    return item.idea_id ? '<a data-cmd="step_idea" data-idea="' + item.idea_id + '" data-name="'
                                          + item.name + '">' + item.name + '</a>' : item.name;
                },
                "sortable": 1
            },
            {
                "field": "impression",
                "title": "展现数",
                "content": mf.getFieldContentLess('impression'),
                "sortable": 1,
                "align": "right"
            },
            {
                "field": "click",
                "title": "点击数",
                "content": mf.getFieldContentLess('click'),
                "sortable": 1,
                "align": "right"
            },
            {
                "field": "cost",
                "title": "消费",
                "content": mf.getFieldContentMoney('cost'),
                "sortable": 1,
                "align": "right"
            },
            {
                "field": "ctr",
                "title": "点击率",
                "content": mf.getFieldContentPercent('ctr'),
                "sortable": 1,
                "align": "right"
            },
            {
                "field": "cpm",
                "title": "千次展现消费",
                "content": mf.getFieldContentMoney('cpm'),
                "sortable": 1,
                "align": "right"
            }
        ];
    };
    mf.index.report.model.dayIdea = new er.Model({
        QUERY_MAP: {},
        LOADER_LIST: ['modelLoader'],
        modelLoader: new er.Model.Loader(function () {
            console.log('modelLoader');
            var loader = this;
            var unitID = loader.get('unit');

            loader.stop();
            mf.parallelAjax([
                {
                    url: '/config',
                    cache: true
                },
                {
                    url: '/idea?unit=' + unitID
                },
                {
                    url: '/report/cost/' + loader.get('date').replace(/\-/g, '') + '-'
                         + loader.get('date').replace(/\-/g,
                        ''),
                    errorMessage: '分创意单日数据获取失败，请重试。'
                }
            ], function (config, ideas, reportData) {

                loader.set('config', config);
                var reportMap = {};
                for (var date in reportData) {
                    reportData[date].filter(function (n) {
                        return n.unit_id == unitID;
                    }).forEach(function (data) {
                        var item = reportMap[data.idea_id] = reportMap[data.idea_id] || {
                                plan_id: data.plan_id,
                                unit_id: data.unit_id,
                                idea_id: data.idea_id,
                                date: date,
                                list: []
                            };
                        item.impression = (item.impression || 0) + data.impression;
                        item.cost = (item.cost || 0) + data.cost;
                        item.click = (item.click || 0) + data.click;
                        item.list.push(data);
                    });
                }
                var reportList = $.map(reportMap, function (n) {
                    n.cost = n.cost || 0;
                    n.click = n.click || 0;
                    n.impression = n.impression || 0;
                    n.cost /= 1000;
                    n.ctr = (n.impression ? n.click / n.impression : 0 ) * 100;
                    n.cpm = (n.impression ? n.cost / n.impression : 0) * 1000;
                    var r = mf.m.utils.deepSearch(ideas, n.idea_id, 'id');
                    n.name = r ? r.name : '未知';
                    return n;
                });
                var orderBy = 'cost';
                reportList.sort(function (a, b) {
                    return a[orderBy] > b[orderBy] ? -1 : 1;
                });

                loader.set('order', 'desc');
                loader.set('orderBy', orderBy);
                loader.set('lists', reportList);
                loader.set('ideas', ideas);
                loader.set('fields', FIELDS(loader));

                loader.start();
            });
        })
    });
})();