(function () {
    mf.index.index.register = new er.Action({
        model: mf.index.index.model.register,
        view: new er.View({
            template: 'mf_index_index_register',
            UI_PROP: {}
        }),
        STATE_MAP: {},

        onenter: function () {
            console.log('onenter');
            mf.onenter();
            $('body').addClass('login-modal');
        },
        onafterrepaint: function () {
            console.log('onafterrepaint');
        },
        onafterrender: function () {
            console.log('onafterrender');
            var action = this;
            var V = mf.m.validate();

            //mf.fixPassword('password', 'show_password');
            //if (action.model.get('pwd')) {
            //    $(esui.get('show_password').main).hide();
            //    $(esui.get('password').main).show();
            //}

            V.reg(
                {
                    id: 'username',
                    rule: [
                        {
                            tpl: 'notNull', msg: '登录账号不能为空'
                        }
                    ]
                }
            );
            V.reg(
                {
                    id: 'password',
                    rule: [
                        {
                            tpl: 'notNull', msg: '密码不能为空'
                        },
                        {
                            tpl: 'limitS', min: 6, msg: '密码不能少于6位'
                        }
                    ]
                }
            );
            V.reg(
                {
                    id: 'display_name',
                    rule: [
                        {
                            tpl: 'notNull', msg: '用户名不能为空'
                        },
                        {
                            tpl: 'limit', min: 2, msg: '用户名不能过短'
                        }
                    ]
                }
            );
            V.reg(
                {
                    id: 'email',
                    rule: [
                        {
                            tpl: 'email', msg: '邮箱输入有误'
                        }
                    ]
                }
            );
            V.reg(
                {
                    id: 'phone',
                    rule: [
                        {
                            tpl: 'notNull', msg: '手机号不能为空'
                        },
                        {
                            tpl: 'regex', regx: V.regexp.phone, msg: '手机号输入有误'
                        }
                    ]
                }
            );
            V.reg(
                {
                    id: 'captcha',
                    rule: [
                        {
                            tpl: 'notNull', msg: '验证码不能为空'
                        }
                    ]
                }
            );
            V.reg(
                {
                    id: 'agreement',
                    rule: [
                        {
                            tpl: 'notNull', msg: '请阅读并同意注册协议'
                        }
                    ]
                }
            );
            var infoDialog;
            esui.get('submit').onclick = function () {
                if (!action.validateForm() || !V.check()) {
                    return;
                }
                mf.loading();
                mf.parallelAjax(
                    {
                        url: '/register',
                        type: 'POST',
                        //requestType: 'form',
                        data: {
                            username: esui.get('username').getValue(),
                            password: esui.get('password').getValue(),
                            display_name: esui.get('display_name').getValue(),
                            email: esui.get('email').getValue(),
                            phone: esui.get('phone').getValue(),
                            qq: esui.get('qq').getValue(),
                            captcha: esui.get('captcha').getValue()
                        }
                    },
                    function (result) {
                        // 暂时这样吧，用户信息以后再加入
                        var user = {
                            authority: 255
                        };
                        user.authority = parseInt(user.authority || 0);
                        if (user.authority > 0) {
                            user.authority |= 1;
                        }

                        var expObj = {expires: 3600000 * 24 * 15};
                        T.cookie.set(mf.cookieKeyMap.name, esui.get('display_name').getValue(), expObj);
                        T.cookie.set(mf.cookieKeyMap.authority, user.authority);
                        mf.authority.parse(user.authority);
                        infoDialog = esui.Dialog.alert(
                            {
                                title: '注册成功',
                                content: '您已注册成功<br>' +
                                         '登录账户：' + esui.get('username').getValue() + '<br>' +
                                         '登录密码：' + esui.get('password').getValue(),
                                onok: mf.m.utils.nextTickWrapper(function () {
                                    checkLogin();
                                })
                            }
                        );
                    }
                ).fail(function () {
                        $('#captcha_img').trigger('click');
                    });
            };

            $('body').unbind('keypress.login')
                .bind(
                'keypress.login', function (e) {
                    if (e.keyCode === 13) {
                        if (infoDialog) {
                            infoDialog.oncommand();
                            infoDialog = null;
                        } else {
                            esui.get('username').main.blur();
                            esui.get('password').main.blur();
                            esui.get('submit').onclick();
                        }
                    }
                });
            $('#agreement_content').click(function () {
                mf.m.utils.writeInNewWindow(mf.etplFetch('agreement_content'), {
                    noWrapper: 1,
                    type: 'html'
                });
            });
            $('#captcha_img').click(function () {
                this.src = '/captcha?t=' + (new Date().getTime());
                $(this).show();
            }).trigger('click');

            esui.get('back').onclick = mf.m.utils.nextTickWrapper(function () {
                er.locator.redirect('/index/login');
            });
        },
        onentercomplete: function () {
            mf.loaded();
        },
        onleave: function () {
            $('body').unbind('keypress.login').unbind('keypress.dialog');
        }
    });
})();