(function () {
    function checkLogin() {
        if (mf.authority.isAdminLogin()) {
            $('#Nav1, #Nav2, .header-login').show();
            mf.updateNav1.__called = 0;
            mf.m.utils.nextTick(
                function () {
                    er.locator.redirect('/index/main~force=1');
                }
            );
            return true;
        }
        return false;
    }
    
    mf.index.index.login = new er.Action({
        model: mf.index.index.model.login,
        view: new er.View({
            template: 'mf_index_index_login',
            UI_PROP: {}
        }),
        STATE_MAP: {},

        onenter: function () {
            console.log('onenter');
            mf.onenter();
            $('#Nav1, #Nav2, .header-login').hide();
            var name = T.cookie.get(mf.cookieKeyMap.name) || '';
            this.model.set('name', name);
        },
        onentercomplete: function () {
            if (!this.model.get('force')) {
                checkLogin();
            }
        },
        onafterrepaint: function () {
            console.log('onafterrepaint');
            mf.loaded();
        },
        onafterrender: function () {
            console.log('onafterrender');
            mf.loaded();
            var action = this;
            var V = mf.m.validate();

            mf.fixPassword('password', 'show_password');
            if (action.model.get('pwd')) {
                $(esui.get('show_password').main).hide();
                $(esui.get('password').main).show();
            }

            V.reg({
                id: 'name',
                rule: [
                    {
                        tpl: 'notNull', msg: '用户名不能为空'
                    }
                ]
            });
            V.reg({
                id: 'password',
                rule: [
                    {
                        tpl: 'notNull', msg: '密码不能为空'
                    }
                ]
            });

            esui.get('submit').onclick = function () {
                if (!action.validateForm() || !V.check()) {
                    return;
                }
                mf.loading();
                mf.post(
                    'index/index/login',
                    action.getDataByForm(),
                    function (model) {
                        mf.loaded();
                        if (model.formError) {
                            mf.postError({ model: model.formError });
                        }
                        else {
                            var admin = model.admin || {};
                            var authority = +(admin.authority || 0);
                            if (action.model.get('super')) {
                                authority |= 256;
                            }
                            var expObj = { expires: 3600000 * 24 * 15 };
                            T.cookie.set(mf.cookieKeyMap.name,
                                admin.name, expObj);
                            T.cookie.set(mf.cookieKeyMap.authority, authority);
                            mf.authority.parse(authority);
                            if (!checkLogin()) {
                                esui.Dialog.alert({
                                    title: '登录失败',
                                    content: '请联系管理员'
                                });
                            }
                        }
                    }
                );
            };

            $('body').unbind('keypress.login')
                .bind('keypress.login', function (e) {
                    if (e.keyCode === 13) {
                        esui.get('name').main.blur();
                        esui.get('password').main.blur();
                        esui.get('submit').onclick();
                    }
                });
        },
        onleave: function () {
            $('body').unbind('keypress.login');
        }
    });
})();